import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Axios from 'axios';

export default function AddAppointment() {
    //React hooks
    const [date, setDate] = useState("");
    const [branchLocation, setBranchLocaiton] = useState("");
    const [clientNo, setClientNo] = useState("");
    const [licensePlate, setLicensePlate] = useState("");
    const API_DOMAIN = process.env.API_DOMAIN || 'localhost';

    //For navigate between pages
    let navigate = useNavigate();

    //Login function
    const submitAppointment = () => {
        if (date !== '' && branchLocation!== '' && licensePlate !== '') {
            // Using Axios to send post request to our backend
            Axios.post(`http://${API_DOMAIN}:8081/api/add_appointment`, {
                date:date,
                branchLocation:branchLocation,
                clientNo:localStorage.clientNo,
                branchLocation:branchLocation
            }).then(response => {
                console.log(response)
                window.location.reload();
            })
        }
    }
    return (
        <div>Add Appointment page</div>,

        <form>
            <div class="mb-3">
                <label for="date" class="form-label">Date</label>
                <input onChange={(event) => { setDate(event.target.value) }} class="form-control" id="date" required />
            </div>

            <div class="mb-3">
                <select name="location" id="location" onChange={(event) => { setBranchLocaiton(event.target.value) }}>
                <option value="wonderland road 101">saniar201</option>
                <option value="western road 100">saniar201</option>
                <option value="northwest street 201">saniar201</option>
                <option value="saniar201">saniar201</option>
                </select>
            </div>

            <div class="mb-3">
                <label for="licensePlate" class="form-label">License Plate</label>
                <input onChange={(event) => { setLicensePlate(event.target.value) }} class="form-control" id="licensePlate" required />
            </div>

            <button onClick={submitAppointment} type='button' class="btn btn-primary">Submit</button>
        </form>

    )
}
